--SELECT  [week] = replace((SELECT DISTINCT week AS [data()] FROM [WEEKS] FOR xml path('')), ' ', ', '), [request_id] FROM [WEEKS] GROUP BY [request_id]
SELECT  DISTINCT [week] = replace((SELECT DISTINCT week AS [data()] FROM [WEEKS]  FOR xml path('')), ' ', ', ' ) ,[REQUESTS].[request_id], [REQUESTS].[module], [REQUESTS].[room_code], [REQUESTS].[capacity], [REQUESTS].[wheelchair], [REQUESTS].[projector], [REQUESTS].[visualiser], [REQUESTS].[whiteboard], [REQUESTS].[computer], [REQUESTS].[lecture_capture], [REQUESTS].[pa_system], [REQUESTS].[radio_microphone], [REQUESTS].[video_dvd], [REQUESTS].[arrangement], [REQUESTS].[special_req], [REQUESTS].[priority], [REQUESTS].[day], [REQUESTS].[period], [REQUESTS].[duration], [REQUESTS].[status], [REQUESTS].[park], [REQUESTS].[semester], [REQUESTS].[year], [REQUESTS].[lecturer], [REQUESTS].[dept_code], [REQUESTS].[session] FROM [REQUESTS], [WEEKS]  WHERE [REQUESTS].[dept_code] = 'CO' AND [REQUESTS].[request_id] = [WEEKS].[request_id] 